class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, item):
        self.stack1.append(item)

    def dequeue(self):
        if not self.is_empty():
            return self.stack1.pop()
        else:
            return self.stack2.pop()

    def is_empty(self):
        return len(self.stack1) == 0

    def peek(self):
        if not self.is_empty():
            return self.stack1[0]
        else:
            return self.stack2[0]

    def pop(self):
        if not self.is_empty():
            return self.stack1.pop()
        else:
            return self.stack2.pop()

    def push(self, item):
        self.stack2.append(item)

    def update(self, other_queue):
        self.stack1.extend(other_queue.stack1)
        self.stack2.extend(other_queue.stack2)

    def __str__(self):
        return str(self.peek())

    def __repr__(self):
        return "Queue: {0}".format(self.peek())
```

